@Component({
  selector: 'app-article-edit',
  templateUrl: './article-edit.component.html',
  styleUrls: ['./article-edit.component.css']
})
export class ArticleEditComponent implements OnInit, OnDestroy {
  structure$: Observable<Field[]>;
  data$: Observable<Article>;

  constructor(private store: Store) {
    this.structure$ = this.store.select(ngrxFormsQuery.selectStructure);
    this.data$ = this.store.select(ngrxFormsQuery.selectData);
  }

  ngOnInit() {
    this.store.dispatch(formsActions.setStructure({ structure: this.structure }));
    this.data$.subscribe(data => {
      if (data) {
        this.store.dispatch(formsActions.setData({ data }));
      }
    });
  }

  ngOnDestroy() {
    this.store.dispatch(formsActions.initializeForm());
  }

  updateForm(changes: any) {
    this.store.dispatch(formsActions.updateData({ changes }));
  }

  submit() {
    this.data$.subscribe(data => {
      const articleData = { ...data, tagList: data.tagList.split(',').map(tag => tag.trim()) };
      this.store.dispatch(articleEditActions.publishArticle({ article: articleData }));
    });
  }

  structure: Field[] = [
    {
      type: 'INPUT',
      name: 'title',
      placeholder: 'Article Title',
      validation: { required: true }
    },
    {
      type: 'INPUT',
      name: 'description',
      placeholder: 'What is this article about?',
      validation: { required: true }
    },
    {
      type: 'TEXTAREA',
      name: 'body',
      placeholder: 'Write your article (in markdown)',
      validation: { required: true }
    },
    {
      type: 'INPUT',
      name: 'tagList',
      placeholder: 'Enter tags'
    }
  ];
}
```

